document.addEventListener("DOMContentLoaded",function(){const url=new URL(window.location.href);const isHome=url.pathname==="/";const hasSearch=url.searchParams.has("search");const hasPage=url.searchParams.has("page");const hasCategory=url.searchParams.has("category");if(isHome&&(hasSearch||hasPage||hasCategory)){const target=document.getElementById("cars-list");if(target){target.scrollIntoView({behavior:"smooth"});}}
setTimeout(()=>{document.querySelectorAll(".page-toast").forEach((el)=>{el.remove();});},5000);function closeModal(){const modal=bootstrap.Modal.getInstance(document.getElementById("confirm-modal"));modal.hide();}
function loaderToggel(hide){const loader=document.getElementById("loader");const modal=document.getElementById("confirm-modal");loader.style.visibility=hide?"hidden":"visible";modal.style.zIndex=hide?"1100":"10";}});;let items=document.querySelectorAll(".carousel .carousel-item");items.forEach((el)=>{const minPerSlide=4;let next=el.nextElementSibling;for(var i=1;i<minPerSlide;i++){if(!next){next=items[0];}
let cloneChild=next.cloneNode(true);el.appendChild(cloneChild.children[0]);next=next.nextElementSibling;}});;